def network = responseHolder.getNodeValue("//user:searchOnlineAccounts_2Response//user:userDetailRecord/user:legacyAandRCustomerData/user:network")
log.info "network : " + network   

def dqlistingdetail = responseHolder.getNodeValue("//user:searchOnlineAccounts_2Response//user:userDetailRecord/user:legacyAandRCustomerData/user:additionalAttributes/user:directoryEntryType")
log.info "dqlistingdetail : " + dqlistingdetail 


def dqlistinglevel = responseHolder.getNodeValue("//user:searchOnlineAccounts_2Response//user:userDetailRecord/user:legacyAandRCustomerData/user:additionalAttributes/user:directoryEntryListingLevel")
log.info "dqlistingdetail : " + dqlistinglevel  

def anrusername = responseHolder.getNodeValue("//user:searchOnlineAccounts_2Response//user:userDetailRecord/user:identityData/user:identityUsername")
log.info "anrusername: " + anrusername  

assert statusCode == expStatusCode
assert successMsg == expSuccessMessage
assert identityUID == "13000000000000830973"
assert custNum ==  "11742097"
assert portalId == "hussain_vhora6189"
assert bfId == "130000000819761"
assert domain == "stf.ref.o2.co.uk"

